<?php	if (!defined("MANAGE_INTERFACE")) exit;		if (!isset($name)){exit("param_not_exist");}	if (!isset($passport)){exit("param_not_exist");}    if (!empty($passport)) {		$user = sql_fetch_one("select * from sys_user u where passport='$passport' limit 1");    } else if (!empty($name)) {		$user = sql_fetch_one("select * from sys_user where name='$name'");    }	$count = sql_fetch_one("select count from sys_goods where uid = ".$user['uid']." and gid = 0");	$user['lijin'] = $count['count'];	$union_id = $user['union_id'];	if($union_id==0)$unionname = '没有联盟';	else $unionname=sql_fetch_one_cell("select name from sys_union where id=".$union_id);	$user['unionname']=$unionname;	$passport=$user['passport'];	$ret['paylog'] = sql_fetch_rows("select from_unixtime(time) as logtime,money from pay_log where passport='$passport' order by time desc");	$ret['user']=$user;?>