<?php	if (!defined("MANAGE_INTERFACE")) exit;	if (!isset($inPath)){exit("param_not_exist");}		if(!empty($inPath[1]) && $inPath[1] == 2){            $id = $inPath[2];             $ret['campaign_contents'] = sql_fetch_rows("select s.operate_sid as operate_sid,s.id as id,s.enable as enable,s.onsale as onsale,s.price as price,s.rebate as rebate,s.commend as commend,s.hot as hot,s.operate_type as operate_type,FROM_UNIXTIME(s.start_time) as start_time,s.start_time as start_time2,FROM_UNIXTIME(s.end_time) as end_time,s.end_time as end_time2,s.totalCount as totalCount,s.userbuycnt as userbuycnt,s.daybuycnt as daybuycnt,s.description as description,c.name as name,c.oriprice as oriprice from adm_shop_sale s left join cfg_shop c on (s.operate_sid=c.id) where `campaign_id`='$id'");        }        $campaign_lists = sql_fetch_rows("select * from adm_shop_campaign order by id asc");                     $i = 1;        foreach($campaign_lists as &$row){            $start_time = sql_fetch_one_cell("select MIN(FROM_UNIXTIME(start_time)) from adm_shop_sale where `campaign_id`='$row[id]'");                           $end_time = sql_fetch_one_cell("select MAX(FROM_UNIXTIME(end_time)) from adm_shop_sale where `campaign_id`='$row[id]'");                           $row['start_time'] = $start_time;            $row['end_time'] = $end_time;            $row['orderid'] = $i++;        }        $ret['lists'] = $campaign_lists;?>